def gcd(a, b):
    while b:
        a, b = b, a % b
    return abs(a)

class BankAccount:
    def __init__(self, owner, balance=0):
        self.owner = owner
        self.balance = balance

    def deposit(self, amount):
        self.balance += amount
        return self.balance

    def withdraw(self, amount):
        if amount > self.balance:
            raise ValueError('Недостаточно средств')
        self.balance -= amount
        return self.balance

def cache(func):
    _cache = {}
    def wrapper(*args):
        if args not in _cache:
            _cache[args] = func(*args)
        return _cache[args]
    return wrapper

import os

def find_files(path, extension):
    result = []
    for root, dirs, files in os.walk(path):
        for file in files:
            if file.endswith(extension):
                result.append(os.path.join(root, file))
    return result

def fibonacci():
    a, b = 0, 1
    while True:
        yield a
        a, b = b, a + b

import time

class Timer:
    def __enter__(self):
        self.start = time.time()
        return self

    def __exit__(self, *args):
        self.end = time.time()
        print(f'Время выполнения: {self.end - self.start:.2f} сек')

import json

def read_json(filename):
    try:
        with open(filename, 'r') as f:
            return json.load(f)
    except (FileNotFoundError, json.JSONDecodeError) as e:
        print(f'Ошибка: {e}')
        return None

def binary_search(arr, target):
    low = 0
    high = len(arr) - 1

    while low <= high:
        mid = (low + high) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            low = mid + 1
        else:
            high = mid - 1
    return -1

def факториал(n):
    if n == 0:
        return 1
    return n * факториал(n-1)

def простое_ли(число):
    if число < 2:
        return False
    for i in range(2, int(число**0.5)+1):
        if число%i == 0:
            return False
    return True

import os

def найти_хвосты(путь, хвост):
    найденное = []
    for root, _, files in os.walk(путь):
        for file in files:
            if file.endswith(хвост):
                найденное.append(os.path.join(root, file))
    return найденное

def фибоначчи():
    a, b = 0, 1
    while True:
        yield a
        a, b = b, a + b
